(11, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''freebsd, ports, sysinstall, cvsup, atualização''),UCASE(''FreeBSD - Atualização''),''Atualização do FreeBSD'',''sysinstall-> Configure-> Packages-> FTP-> Brazil<br>\r\nNet-> cvsup-without-gui-xxx ---> Install, depois Exit Install<br>\r\nstandard-supfile: atualiza o ports<br>\r\n\r\n'',''n'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
(12, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''ssh, keygen''),UCASE(''FreeBSD - SSH''),''Como gerar chaves e autorizar acesso via ssh.'',''Geração de chave de servidor e workstation<br>\r\n# ssh-keygen -t dsa <p>\r\nS&atilde;o criados os arquivos id_dsa e id_dsa.pub dentro do diretório .ssh<br>\r\nEnviar o arquivo id_dsa.pub para a m&aacute;quina que vai permitir acesso via SSH.\r\nCopiar a chave para o servidor Web via ssh_FTP.<br>\r\nAcessar o diretorio /root/.ssh e baixar a chave do servidor web com o comando:<br>\r\n# fetch http://www.clp.ubesp.br/chave.pub<p>\r\nCaso n&atilde;o exista criar o arquivo authorized_keys:<br>\r\n# cat >> authorized_keys <p>\r\nIncluir a chave copiada no authorized_keys:<br>\r\n# cat chave.pub >> authorized_keys'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
(13, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''rede, DNS, hostname''),UCASE(''FreeBSD - Configuração de Rede''),''Arquivos de configuração da rede.'',''DNS +\r\n/etc/resolv.conf+\r\n~\r\nMáquina e IP+\r\n/etc/hosts+\r\n~\r\nGateway+\r\n/etc/rc.conf+'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
(14, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''SOURCE, MAKE''),UCASE(''Instalar sources Linux''),''Instalar e desinstalar a partir dos sources .tar.'',''** Instalação ***+\r\n1- Descompactar o source: tar -xzf source.tar.gz +\r\n2- Acessar o diretório: cd diretório_criado +\r\n3- Executar os comandos: +\r\n./configure+\r\nmake+\r\nsudo make install~\r\nPronto! (se não houver problemas de dependências)~\r\n** Desinstalação ***+\r\n1- Acessar o diretório: cd diretório_criado +\r\n2- sudo make uninstall+\r\n2- make clean'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
