(16, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''dhcp, dhcpd, configuração''),UCASE(''FreeBSD - DHCPD''),''Arquivos de configuração do servidor DHCP.'',''**/usr/local/etc/dhcpd.conf*** ~\r\n\r\noption domain-name "clp.unesp.br";+\r\noption domain-name-servers 200.145.51.1,200.145.1.1;+\r\ndefault-lease-time 3600;+\r\nmax-lease-time 86400;+\r\nddns-update-style none;+\r\n~\r\nsubnet 200.145.52.0 netmask 255.255.255.0 {+\r\n	range 200.145.52.111 200.145.52.160;+\r\n	option routers 200.145.52.10;+\r\n}+'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
