(21, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''openoffice, broffice, writer''),UCASE(''OpenOffice - orientação página''),''Alterar a orientação da página no writer'',''1 - Inserir uma Quebra manual...+\r\n2 - Quebra de página -> Estilo : pág. direita, pág. esquerda, primeira pág.+\r\n3 - Ir para a pág. que mudará a orientação+\r\n4 - Formatar -> Página, escolher orientação para a desejada+'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
(22, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''nagios, monitoramento, serviços''),UCASE(''FreeBSD - Nagios''),''Exemplo de arquivo de configuração de hosts.'',''### Definição do Host ###+\r\ndefine host{+\r\n       use                     generic-host            +\r\n       host_name               porchat+\r\n       alias                   porchat+\r\n       address                 200.145.51.5+\r\n       check_command           check-host-alive+\r\n       max_check_attempts      2+\r\n       check_interval          1+\r\n       notification_interval   30+\r\n       notification_period     24x7+\r\n       notification_options    d,u,r+\r\n       contact_groups          admins+\r\n}+\r\n+\r\n### PING ###+\r\ndefine service{+\r\n       host_name                       porchat+\r\n       service_description             PING+\r\n       check_command                   check_ping!5000.0,80%!8000.0,100%+\r\n       max_check_attempts              2+\r\n       normal_check_interval           1+\r\n       retry_check_interval            1+\r\n       check_period                    24x7+\r\n       notification_interval           30+\r\n       notification_period             24x7+\r\n       notification_options            w,c,r+\r\n       contact_groups                  admins+\r\n}+\r\n+\r\n# check that web services are running+\r\ndefine service {+\r\n        host_name	                porchat+\r\n        service_description             HTTP+\r\n 	check_command                   check_http+\r\n        use                             generic-service+\r\n	notification_interval           0 ; set > 0 if you want to be renotified+\r\n        contact_groups                  admins+\r\n}+\r\n+\r\n# check that ssh services are running+\r\ndefine service {+\r\n        host_name	                pensil+\r\n        service_description             SSH+\r\n	check_command                   check_ssh+\r\n        max_check_attempts              2+\r\n        normal_check_interval           1+\r\n        retry_check_interval            1+\r\n        check_period                    24x7+\r\n	notification_interval           0 ; set > 0 if you want to be renotified+\r\n        contact_groups                  admins+\r\n}'',''n'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
(23, '', 'UPDATE caso SET solucao=''### Definição do Host ###+\r\ndefine host{+\r\n       use                     generic-host +\r\n       host_name               **nome_host***+\r\n       alias                   **nome_alias***+\r\n       address                 **ip_host***+\r\n       check_command           check-host-alive+\r\n       max_check_attempts      2+\r\n       check_interval          1+\r\n       notification_interval   30+\r\n       notification_period     24x7+\r\n       notification_options    d,u,r+\r\n       contact_groups          admins+\r\n}+\r\n+\r\n### PING ###+\r\ndefine service{+\r\n       host_name                       **nome_host***+\r\n       service_description             PING+\r\n       check_command                   check_ping!5000.0,80%!8000.0,100%+\r\n       max_check_attempts              2+\r\n       normal_check_interval           1+\r\n       retry_check_interval            1+\r\n       check_period                    24x7+\r\n       notification_interval           30+\r\n       notification_period             24x7+\r\n       notification_options            w,c,r+\r\n       contact_groups                  admins+\r\n}+\r\n+\r\n# check that web services are running+\r\ndefine service {+\r\n        host_name	                **nome_host***+\r\n        service_description             HTTP+\r\n 	check_command                   check_http+\r\n        use                             generic-service+\r\n	notification_interval           0 ; set > 0 if you want to be renotified+\r\n        contact_groups                  admins+\r\n}+\r\n+\r\n# check that ssh services are running+\r\ndefine service {+\r\n        host_name	                **nome_host***+\r\n        service_description             SSH+\r\n	check_command                   check_ssh+\r\n        max_check_attempts              2+\r\n        normal_check_interval           1+\r\n        retry_check_interval            1+\r\n        check_period                    24x7+\r\n	notification_interval           0 ; set > 0 if you want to be renotified+\r\n        contact_groups                  admins+\r\n}'',solucionado=''s'' WHERE id_casos=''12''', '0000-00-00 00:00:00', '200.145.51.37'),
(24, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''nagios''),UCASE(''Nagios''),''Comandos para monitoramento de serviços'',''<pre>\r\n##HTTP#\r\ndefine service{+\r\n	use		generic-service\r\n	host_name		remotehost\r\n	service_description	HTTP\r\n	check_command	check_http\r\n	}\r\n\r\n##SMTP##\r\ndefine service{\r\n	use			generic-service\r\n	host_name		remotehost\r\n	service_description	SMTP\r\n	check_command		check_smtp\r\n	}\r\n\r\n##POP#\r\ndefine service{\r\n	use			generic-service\r\n	host_name		remotehost\r\n	service_description	POP3\r\n	check_command		check_pop\r\n	}\r\n\r\n##FTP#\r\ndefine service{\r\n	use			generic-service\r\n	host_name		remotehost\r\n	service_description	FTP\r\n	check_command		check_ftp\r\n	}\r\n</pre>'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
