(41, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''mysql, retro-inserção''),UCASE(''MYSQL - INSERÇÃO A PARTIR DE SELECT''),''Inserir dados a partir de outra tabela'',''insert into tempnews (\r\n     News_Id,\r\n     News_Title, \r\n     News_Item, \r\n     date,\r\n     Id_Mod,\r\n     Id_Cat\r\n) select \r\n     News_Id,\r\n     News_Title,\r\n     News_Item,\r\n     date,\r\n     Id_Mod,\r\n     Id_Cat\r\nfrom tblnews \r\norder by date desc\r\nlimit 0,100'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
(42, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''impressão, rede, ms-dos''),UCASE(''Impressão em rede pelo DOS''),''Imprimir arquivo pelo MS-DOS em impressora de rede'',''Modo 1+\r\nprint /d:\\\\pserver\\laser1 unidade:\\arquivo.ext+\r\n\r\nModo 2~\r\nnet use lptX \\\\server\\queue'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
(43, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''nagios, monitoramento''),UCASE(''NAGIOS - Informações''),''Informações sobre a configuração do Nagios'',''Diretório de itens monitorados+\r\n**/usr/local/etc/nagios/objects***~\r\n\r\nReiniciando o serviço+\r\n**/usr/local/etc/rc.d/nagios restart***'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
(44, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''down ports, config, ''),UCASE('' Switches 3Com - Portas desabilitadas STP''),''Portas desabilitadas pelo STP como sendo Bridge ou suspeitas de gerar loop e congestionar a rede.'',''Acessar como sys~\r\n<3COM> sys+\r\n**Exibir as portas desabilitadas***~\r\n[3COM] display stp down-port+\r\n.......+\r\n[3COM] int gig1/0/XX+\r\n[3COM] stp edge-port disable+\r\n[3COM] undo shutdown+\r\n'',''n'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
