(46, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''config, ip, switch, 3com''),UCASE(''Switches 3Com - configuração IP''),''Configurando IP\r\n'',''<> system+\r\n[] interface vlan 1+\r\n[] ip address <ip> <netmask>+\r\n[] quit'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
(47, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''configuração, arquivos, ftp''),UCASE(''Switvhes 3Com - Carregando arquivos para o 4500''),''Procedimentos para carregar arquivos de configuração, firmware e chaves ssh.'',''Ativar um servidor TFTP (pode ser o solarwinds utilizado no curso).~\r\n**Não esquecer de fazer as alterações necessárias no .cfg***\r\nEm user view executar os seguintes comandos:~\r\n<4500> tftp {ip_ftp} get arquivo.xxx+\r\n**repetir para todos os arquivos incluindo as chaves públicas ssh***~\r\nCarregando o arquivo de configuração para o startup+\r\n<4500> startup saved-configuration arquivo.cfg main~\r\nCarregando o firmware como principal+\r\n<4500> boot-loader file arquivo.app main~\r\nDefinindo o bootrom+\r\n<4500> bootrom update file arquivo.btm~\r\nGerando a chave do switch+\r\n<4500> public-key local create rsa'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
(48, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''broffice, configuração''),UCASE(''BrOffice - Orientação da Página''),''Como ter duas ou mais orientações de página num documento.'',''1 - Inserir uma Quebra manual...+\r\n2 - Quebra de página -> Estilo : pág. direita, pág. esquerda, primeira pág.+\r\n3 - Ir para a pág. que mudará a orientação+\r\n4 - Formatar -> Página, escolher orientação para a desejada+'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.37'),
