(74, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''squirrelmail, postifix, webmail, abuse''),UCASE(''E-MAIL BANIDO''),''Remover usuário banido pelo ABUSE do webmail.'',''Acessar o diretório:+\r\n**cd /usr/local/www/squirreloutlook/data/***~\r\nEditar o arquivo de preferência do usuário banido e remover a linha **USER_IS_POSSIBLE_SPAMMER***+\r\n**edit usuário.pref***'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.69'),
