(90, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''bacula; backup; cliente''),UCASE(''BACULA - INSTALAÇÃO CLIENTES''),''Instalação e configuração dos clientes.'',''### INSTALAÇÃO E CONFIGURAÇÃO NOS CLIENTES ~ \r\n**####  NO FREEBSD - INSTALANDO***~ \r\n**cd /usr/ports/sysutils/bacula-client~ \r\nmake install clean***~ \r\n~ \r\n##### CONFIGURANDO~ \r\n**/usr/local/etc/bacula-fd.conf***~ \r\n~ \r\n##### INICIANDO O SERVIÇO~ \r\n**/usr/local/etc/rc.d/bacula-fd onestart***~ \r\n~ \r\n### OU~ \r\n~ \r\n**/usr/local/etc/rc.d/bacula-fd start***~ \r\n~\r\n**#### DEBIAN/UBUNTU - INSTALANDO***~ \r\n**apt-get install bacula-client***~ \r\n~ \r\n##### CONFIGURANDO~ \r\n**/etc/bacula/bacula-fd.conf***~ \r\n~ \r\n##### INICIANDO O SERVIÇO~ \r\n**/etc/init.d/bacula-fd onestart***~ \r\n~ \r\n### CONFIGURAÇÃO BÁSICA~ \r\n**Director {+\r\n  Name = Diretor+\r\n  Password = "SENHA"+\r\n}~\r\nFileDaemon {+\r\n  Name = ServNOME+\r\n  FDport = 9102 +\r\n  WorkingDirectory = /var/db/bacula+\r\n  Pid Directory = /var/run+\r\n  Maximum Concurrent Jobs = 1+\r\n  FDAddress = IP_ADDRESS+\r\n}~ \r\n\r\nMessages {+\r\n  Name = Standard+\r\n  director = ServNOME-dir = all, !skipped, !restored+\r\n}'',''n'',''3'')', '0000-00-00 00:00:00', '200.145.51.69'),
(91, '', 'UPDATE caso SET solucao=''### INSTALAÇÃO E CONFIGURAÇÃO NOS CLIENTES ~ \r\n**####  NO FREEBSD - INSTALANDO***~ \r\n**cd /usr/ports/sysutils/bacula-client~ \r\nmake install clean***~ \r\n~ \r\n##### CONFIGURANDO~ \r\n**/usr/local/etc/bacula-fd.conf***~ \r\n~ \r\n##### INICIANDO O SERVIÇO~ \r\n**/usr/local/etc/rc.d/bacula-fd onestart***~ \r\n~ \r\n### OU~ \r\n~ \r\n**/usr/local/etc/rc.d/bacula-fd start***~ \r\n~\r\n**#### DEBIAN/UBUNTU - INSTALANDO***~ \r\n**apt-get install bacula-client***~ \r\n~ \r\n##### CONFIGURANDO~ \r\n**/etc/bacula/bacula-fd.conf***~ \r\n~ \r\n##### INICIANDO O SERVIÇO~ \r\n**/etc/init.d/bacula-fd onestart***~ \r\n~ \r\n### CONFIGURAÇÃO BÁSICA~ \r\n**Director {+\r\n  Name = Diretor+\r\n  Password = "SENHA"+\r\n}~\r\nFileDaemon {+\r\n  Name = ServNOME+\r\n  FDport = 9102 +\r\n  WorkingDirectory = /var/db/bacula+\r\n  Pid Directory = /var/run+\r\n  Maximum Concurrent Jobs = 1+\r\n  FDAddress = IP_ADDRESS+\r\n}~ \r\n\r\nMessages {+\r\n  Name = Standard+\r\n  director = ServNOME-dir = all, !skipped, !restored+\r\n}'',solucionado=''s'' WHERE id_casos=''46''', '0000-00-00 00:00:00', '200.145.51.69'),
(92, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''bacula; backup; cliente''),UCASE(''BACULA - CONFIGURAÇÃO DE CLIENTES NO SERVER''),''Arquivo de inclusão da configuração dos clientes.'',''### DEFINIÇÃO DO CLIENTE~ \r\n**Client {~ \r\nName = ServNOME~ \r\nAddress = IP_ADDRESS~ \r\nFDPort = 9102~ \r\nCatalog = MyCatalog~ \r\nPassword = \\"SENHA\\"~ \r\nFile Retention = 90 days~ \r\nJob Retention = 1 year~ \r\nAutoPrune = yes~ \r\n}***~ \r\n~ \r\n### CONJUNTO DE ARQUIVOS ~ \r\n**FileSet {~ \r\n  Name = ServNOME-fs~ \r\n  Include {~ \r\n   Options {~ \r\n   # Opção de segurança~ \r\n   signature = MD5~ \r\n   compression=GZIP9~ \r\n   wildfile = \\"*.tmp\\"~ \r\n   Exclude = yes~ \r\n  }~ \r\n#### Define quais diretórios farão parte do backup~ \r\n  **File = /export/backup/db~ \r\n  File = /etc~ \r\n  File = /usr/local/etc~ \r\n  File = /export/www_root~ \r\n  File = /root~ \r\n  }~ \r\n}***~ \r\n~ \r\n### TAREFAS DE BACKUP E RESTORE~ \r\n**Job {		# BACKUP~ \r\n  Name = ServNOME-bck~ \r\n  Type = Backup~ \r\n  Level = Incremental~ \r\n  Client = ServNOME~ \r\n  FileSet = ServNOME-fs~ \r\n  Storage = Storage~ \r\n  Schedule = ServNOME-sch~ \r\n  Pool = Semanal~ \r\n  Messages = Mensagens~ \r\n}~ \r\n~ \r\nJob {		# RESTORE~ \r\n  Name = ServNOME-rstr~ \r\n  Type = Restore~ \r\n  Client = ServNOME~ \r\n  FileSet = ServNOME-fs~ \r\n  Storage = Storage~ \r\n  Pool = Semanal~ \r\n  Messages = Mensagens~ \r\n}***~ \r\n~ \r\n### AGENDAMENTO~ \r\n**Schedule {~ \r\n  Name = ServNOME-sch~ \r\n  Run = Level=Full		Pool=Mensal 1st Monday at 1:00pm~ \r\n  Run = Level=Differential 	Pool=Semanal 2nd 3rd 4th 5th Monday at 1:00am~ \r\n  Run = Level=Differential 	Pool=Diario sun sat at 1:00am~ \r\n}***'',''n'',''3'')', '0000-00-00 00:00:00', '200.145.51.69'),
