(104, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''bacula; backup; interface''),UCASE(''BACULA - CONFIGURAÇÃO DO BWEB''),''Arquivo de configuração do BWeb, interface web.'',''\\$VAR1 = bless( {\r\n                 \\''display_log_time\\'' \\=\\> 0,\r\n                 \\''enable_security\\'' \\=\\> 0,\r\n                 \\''graph_font\\'' \\=\\> \\''/usr/share/fonts/truetype/ttf-bitstream-vera/Vera.ttf\\'',+\r\n                 \\''name\\'' \\=\\> undef,+\r\n                 \\''config_file\\'' \\=\\> \\''/etc/bacula/bweb.conf\\'',+\r\n                 \\''bconsole\\'' \\=\\> \\''/usr/bin/sudo /usr/bin/bconsole -n -c /etc/bacula/bconsole.conf\\'',+\r\n                 \\''fv_write_path\\'' \\=\\> \\''/var/spool/bweb\\'',+\r\n                 \\''password\\'' \\=\\> \\''SENHA\\'',+\r\n                 \\''template_dir\\'' \\=\\> \\''/usr/share/bweb/tpl\\'',+\r\n                 \\''dbi\\'' \\=\\> \\''DBI:mysql:database=bacula\\'',+\r\n                 \\''enable_security_acl\\'' \\=\\> 0,+\r\n                 \\''error\\'' \\=\\> \\''\\'',+\r\n                 \\''debug\\'' \\=\\> 0,+\r\n                 \\''user\\'' \\=\\> \\''bacula\\'',\r\n                 \\''email_media\\'' \\=\\> \\''email@server.com\\'',+\r\n                 \\''stat_job_table\\'' \\=\\> \\''Job\\''+\r\n               }, \\''Bweb::Config\\'' );'',''n'',''3'')', '0000-00-00 00:00:00', '200.145.51.69'),
