(106, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''bacula; backup; file daemon''),UCASE(''BACULA - CONFIGURAÇÃO DO FILE DAEMON''),''Arquivo de configuração do File Daemon'',''**Director {+\r\nName = Diretor+\r\nPassword = "SENHA"+\r\n}~\r\n\r\nDirector {+\r\nName = diretor-mon+\r\nPassword = \\"SENHA\\"+\r\n}~\r\n\r\nFileDaemon {+\r\nName = Diretor-fd+\r\nFDport = 9102+\r\nWorkingDirectory = /var/lib/bacula+\r\nPid Directory = /var/run/bacula+\r\nMaximum Concurrent Jobs = 10+\r\nFDAddress = IP_ADDRESS+\r\n}~\r\n\r\nMessages {+\r\nName = Standard+\r\ndirector = fdaemon-dir = all, !skipped, !restored+\r\n}***'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.69'),
(107, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''bacula; backup; mídias; bconsole''),UCASE(''BACULA - ADICIONAR MÍDIA''),''Adicionar mídia ao storage pelo Console.'',''# bconsole+\r\nConectando ao Diretor 200.145.51.28:9101+\r\n1000 OK: Diretor Versão: 5.0.1 (24 February 2010)+\r\nDigite um período para cancelar um comando.+\r\n* add+\r\nProvavelmente você não gostaria de ficar usando este comando já que ele+\r\ncria registros do banco de dados sem nomear os Volumes.+\r\nVocê provavelmente quer usar o comando \\"label\\".~\r\n\r\nSelecionado automaticamente Catálogo: MyCatalog+\r\nUsando Catálogo "MyCatalog"+\r\nAgrupamentos definidos:+\r\n     1: Default+\r\n     2: Diario+\r\n     3: Semanal+\r\n     4: Mensal+\r\nSelecione o Agrupamento (1-4): 1+\r\nSelecionado automaticamente Armazenamento: Storage+\r\nEntre o número de Volumes a serem criados. 0=>nome fixo. Max=1000: 1+\r\nEntre o nome do volume base: D1+\r\nInforme o número inicial: 1+\r\n1 Volumes criados no grupo Default+\r\n*'',''n'',''3'')', '0000-00-00 00:00:00', '200.145.51.69'),
(108, '', 'UPDATE caso SET solucao=''# bconsole+\r\nConectando ao Diretor 200.145.51.28:9101+\r\n1000 OK: Diretor Versão: 5.0.1 (24 February 2010)+\r\nDigite um período para cancelar um comando.+\r\n* add+\r\nProvavelmente você não gostaria de ficar usando este comando já que ele+\r\ncria registros do banco de dados sem nomear os Volumes.+\r\nVocê provavelmente quer usar o comando "label".~\r\n\r\nSelecionado automaticamente Catálogo: MyCatalog+\r\nUsando Catálogo "MyCatalog"+\r\nAgrupamentos definidos:+\r\n     1: Default+\r\n     2: Diario+\r\n     3: Semanal+\r\n     4: Mensal+\r\nSelecione o Agrupamento (1-4): 1+\r\nSelecionado automaticamente Armazenamento: Storage+\r\nEntre o número de Volumes a serem criados. 0=>nome fixo. Max=1000: 1+\r\nEntre o nome do volume base: D1+\r\nInforme o número inicial: 1+\r\n1 Volumes criados no grupo Default+\r\n*'',solucionado=''s'' WHERE id_casos=''55''', '0000-00-00 00:00:00', '200.145.51.69'),
(109, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''bacula; backup; problemas''),UCASE(''BACULA - TROUBLESHOOTING''),''Possível problema de gravação no File Storage.'',''Falta de permissão do usuário **\\"bacula\\"*** para gravação no diretório do **File Storage***.+\r\nNo diretório, atribuir direito de gravação ou alterar o propietário.~\r\n\r\n**chown bacula:bacula /dir/FileStorage***\r\n'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.69'),
(110, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''dns; dnssec;''),UCASE(''DNS - ATUALIZAR E ASSINAR''),''Atualizar e assinar alterações no DNS / DNSSEC'',''Executar o script conform abaixo no diretório /root~\r\n** sh dnssec.sh -d clp.unesp.br -f clp.zone***'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.69'),
(111, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''wireless; ap''),UCASE(''USUÁRIOS CONECTADOS WIRELESS''),''Comando usado para saber quantos usuário estão conectados em uma VLAN.'',''display arp vlan <n_vlan>'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.69'),
(112, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''voip; 3com switch;''),UCASE(''SWITCHES 3COM - VoIP CONFIGURAR''),''Como identificar e configurar pontos de rede para VoIP.'',''Identificando o ponto:+\r\nAcessar o switch por SSH e executar o comando abaixo para identificar as portas ativas.~\r\n**<switch> display brief interface***~\r\nIdentifica as portas UP e DOWN, além das VLANS de cada porta.+\r\nPara configurar é necessário mudar para usuário admin com o comando a seguir:~\r\n**<switch> sys***~\r\nAcessar agora a porta que deseja configurar~\r\n**<switch>interface vlan 1/0/NUM_PORTA***~\r\nE executar os comandos abaixo:~\r\n** port link-type hybrid+\r\n undo port hybrid vlan 1+\r\n port hybrid vlan VLAN_NUM untagged+\r\n port hybrid pvid vlan VLAN_NUM+\r\n voice vlan 2 enable ***~\r\n\r\nSó aguardar o telefone carregar as configurações.'',''s'',''3'')', '0000-00-00 00:00:00', '200.145.51.69'),
(113, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''servidores; nobreak''),UCASE(''ESQUEMA NO-BREAK''),''Disposição das fontes dos servidores nas réguas.'',''<tr><td>R1</td><td>S1</td><td>S5</td><td>S6</td><td>S7</td><td>RK</td><td>S2</td><td></td></tr>\r\n<tr><td>R2</td><td>S1</td><td>S3</td><td>S6</td><td>S8</td><td>SW</td><td>S2</td><td>S4</td></tr>\r\n<tr><td>R3</td><td>S3</td><td>S5</td><td>S7</td><td>S8</td><td>DS9</td><td>S4</td><td></td></tr>'',''s'',''3'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
