(115, 'Paulo Roberto d', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''Asterisk;bloquear; ligação; PABX''),UCASE(''Bloquear ligação a cobrar - Asterisk''),''Bloquear ligação a cobrar'',''Acessar a máquina mg (220.145.52.125)\r\ncd /etc/astersk\r\nls -al | grep unesp.ael \r\naparecem os arquivos:\r\nunesp.ael , unesp.ael.old e unesp.ael.rej\r\nabre o arquivo unesp.ael \r\ne procure a palavra cobrar\r\ntem duas linhas comentada\r\n//DgCollectCallBlock();                         // Bloqueia ligacoes a cobrar\r\n//Wait(1);                                      // Espera 1 Seg\r\n\r\nsâo essas linhas ;\r\ndescomente\r\nsalve\r\nexecute o comando rasterisk\r\ne dentro do asterisk execute o comando ael reload\r\ntestar\r\n\r\n\r\n\r\n'',''s'',''1'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
(116, 'Paulo Roberto d', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''configuração; FTP''),UCASE(''Switch D-link - FTP''),''Carregar arquivo de configuração usando linha de comando.'',''download cfg_fromTFTP 200.145.51.56 src_file config_54.cfg dest_file config.cfg'',''s'',''1'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
(117, '', 'UPDATE caso SET solucao=''**Ativar um servidor TFTP (pode ser o solarwinds utilizado no curso).***~\r\n**Não esquecer de fazer as alterações necessárias no .cfg***~\r\n**Em user view executar os seguintes comandos:***~\r\n<4500> tftp {ip_ftp} get arquivo.xxx+\r\n**repetir para todos os arquivos incluindo as chaves públicas ssh***~\r\n**Incluindo a chave na configuração***~\r\n<4500>public-key peer nei.pub import sshkey flash:/chave.pub~\r\n<4500>ssh user valdinei service-type all authentication-type publickey assign publickey nei.pub work-directory flash:/~\r\n**Carregando o arquivo de configuração para o startup***~\r\n<4500> startup saved-configuration arquivo.cfg main~\r\n**------ Caso o firmware não seja o mais recente ------***~\r\n<4500> tftp {ip_ftp} get arquivo.xxx+\r\n**Carregando o firmware como principal***+\r\n<4500> boot-loader file arquivo.app main~\r\n**Definindo o bootrom***+\r\n<4500> bootrom update file arquivo.btm~\r\n**Gerando a chave do switch***+\r\n[4500] public-key local create rsa+\r\n[4500] reboot~\r\n**--------------- Não salvar---------------***'',solucionado=''s'' WHERE id_casos=''27''', '0000-00-00 00:00:00', '2801:88:c17:100'),
(118, 'Paulo Roberto d', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''virtual, VMWare, dump, disk''),UCASE(''VMWare - Copiar disco de real para Virtual''),''Gerar uma máquina virtual a partir de uma real.\r\n'',''1) Criar a máquina virtual\r\n2) bootar o real e o virtual com o live do Ubunto\r\n3) no virtual setar emular IDE e forçar o setup no próximo boot\r\n4) verificar se o ssh esta funcionando em ambas as máquinas\r\n5) configurar as máquinas na mesma rede\r\n6) dd if=/dev/sda | ssh root@192.168.5.1 dd of=/dev/sda\r\nsda (disco todo)\r\n7) fsck -a -y\r\n\r\nls /dev\r\nsudo mount -t ufs /dev/da0s1 /rmp\r\nsudo chmod 777 /tmp/etc/fstab\r\nedit /tmp/etc/fstab\r\nalterar ad0 p/ da0'',''s'',''1'')', '0000-00-00 00:00:00', '200.145.51.65'),
(119, 'Paulo Roberto d', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''Porstfix, certificado''),UCASE(''Postfix - Certificado POP''),''Atualizar certificado POP'',''openssl req -new -x509 -keyout /usr/local/certs/anchieta.key -out /usr/local/certs/anchieta2.crt -days 365\r\n\r\n\r\nopenssl req -new -x509 -keyout /usr/local/etc/apache2/webmail.key -out /usr/local/etc/apache2/webmail.crt -days 365\r\n\r\nopenssl req -new -key anchieta.key -config anchieta2.cnf\r\n\r\nopenssl req -new -config anchieta2.cnf -keyout anchieta2.key'',''s'',''1'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
(120, 'Paulo Roberto d', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''asterisk, reiniciar''),UCASE(''Reiniciar Asterisk''),''Reiniciar o serviço Asterisk'',''Acessar o PABX 200.145.52.126\r\nexecutar o seguinte comando\r\nrasterisk -r -x reload\r\n'',''s'',''1'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
(121, 'Paulo Roberto d', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''certificado, DNSSEC''),UCASE(''Renovar certificado DNSSEC''),''E-mails sendo recusados pela Reitoria.+\r\nO certificado do DNSSEC esta expirado.+'',''comando utilizado+\r\nsh dnssec.sh -d clp.unesp.br -s clp.zone+\r\n'',''s'',''1'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
(122, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''clonar; restaurar;''),UCASE(''CLONAR HD CLONEZILLA''),''Como clonar e restaurar o HD com Clonezilla'',''**Geração da imagem:***\r\n<ol>\r\n<li>Dar o boot com o live cd do Clonezilla;\r\n<li>Escolher o idioma (Portugues);\r\n<li>Escolher a opção **Nao mudar mapa do teclado***;\r\n<li>Iniciar Clonezilla;\r\n<li>Escolher Device/image;\r\n<li>Escolher Local/dev;\r\n<li>Escolher o Disco de destino - sdb1 normalmente (**Não escolher o disco que será clonado - sda ***);\r\n<li>Escolher Modo Iniciante;\r\n<li>Escolher Salvar disco;\r\n<li>Aceitar o nome sugerido;\r\n<li>Escolher -sc skip checking;\r\n<li>Pressionar ENTER e confirmar ENTER.\r\nAguardar a conclusão.\r\n< /ol>\r\n'',''n'',''3'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
