(127, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''ubuntu; pacotes; bbrasil''),UCASE(''Ubuntu Desktop: Essenciais''),''Instalação de pacotes e plugins essenciais ao Ubuntu 12.04 LTS.'',''**Instalação pós Ubuntu~\r\nTodos***\r\n<ul>\r\n<li>sudo apt-get chromium;\r\n<li>sudo apt-get gstreamer;\r\n<li>sudo apt-get flash pluggin;\r\n<li>libreoffice 4.0.3 (usar o script).\r\n</ul>\r\n-----------------------------------------------------\r\n**Banco do Brasil***\r\n<ul>\r\n<li>sudo mkdir -p /etc/.java/.systemPrefs\r\n<li>sudo chmod 755 -R /etc/.java\r\n<li>sudo add-apt-repository ppa:webupd8team/java\r\n<li>sudo apt-get update\r\n<li>sudo apt-get purge openjdk*\r\n<li>sudo apt-get install oracle-java7-installer\r\n</ul>'',''s'',''3'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
(128, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''''),UCASE(''SASD - do caralho''),''Como acessar o SASD?'',''Link:~\r\nhttp://sasd.clp.unesp.br:8080/SASD2/~\r\nou~\r\nhttp://200.145.51.15:8080/SASD2+\r\n\r\nREGRA DO IPTABLES PARA ACESSO DO GBD~\r\niptables -A INPUT -p tcp -s 200.145.208.0/24 --dport 8080 -j ACCEPT\r\n/sbin/iptables-save'',''s'',''3'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
(129, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''vmware; vsphere; update''),UCASE(''VMWare UPDATE''),''Atualização do VSphere'','''',''n'',''3'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
(130, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''bacula; manual; backup''),UCASE(''BACULA - EXECUTAR BACKUP MANUAL''),''Executar o backup manualmente pelo **bconsole***.'',''**# bconsole~\r\n* run+***\r\nDigitar o número do servidor que fará backup+\r\ndigitar **yes***+\r\nQuando terminar, digitar **quit*** para encerrar.\r\n'',''s'',''3'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
(131, 'Paulo Roberto d', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''apache freebsd''),UCASE(''APACHE - REINICIAR''),''Como reinicar o apache no Freebsd'',''/usr/local/etc/rc.d/apache22 restart\r\n'',''s'',''1'')', '0000-00-00 00:00:00', '2801:88:c17:100'),
(132, '', 'UPDATE caso SET solucao=''1) Baixar e instalar o pacote da Pubnet http://intranet.clp.unesp.br/util/Pubnet/Instalador_Pubnet_IOCDA3GemPlus.exe\r\n2) Baixar e instalar o driver do leitor de smartcard OmniKey/GemPC do site http://intranet.clp.unesp.br/util/Pubnet/ e escolher o driver do leitor;~\r\n3) Baixar e instalar o SafeSign do site da imprensa oficial (http://intranet.clp.unesp.br/util/Pubnet/SafeSign/);~\r\n** OBS.: Antes de acessar o site da CertSign deve-se conectar o leitor com o e-CPF ao computador***~\r\n4) Acessar o site da imprensa oficial e testar se tudo está funcionando corretamente https://pubnet.imprensaoficial.com.br/ .~\r\n\r\n3) Acessar o site da CertSign através do link recebido no e-mail;~\r\n5) Escolher o tipo de dispositivo (Leitor de Smartcard) e Enviar;~\r\n6) Digitar o primeiro e o segundo código com 6 dígitos e Enviar;+\r\n- isto criará um certificado associado ao e-CPF.~\r\n7) Acessar o site da Imprensa Oficial (http://sr.imprensaoficial.com.br) e clicar no botão Acessar;~\r\n8) Clicar na opção Configurar e escolher a primeira opção (leitor de smartcard) e deixar em automático, clicar em OK;~\r\n9) Clicar em Login e digitar a senha (PIN) fornecida para o e-CPF.'',solucionado=''s'' WHERE id_casos=''18''', '0000-00-00 00:00:00', '2801:88:c17:100'),
(133, '', 'UPDATE caso SET solucao=''1) Baixar e instalar o pacote da Pubnet http://intranet.clp.unesp.br/util/Pubnet/Instalador_Pubnet_IOCDA3GemPlus.exe\r\n2) Baixar e instalar o driver do leitor de smartcard OmniKey/GemPC do site http://intranet.clp.unesp.br/util/Pubnet/ e escolher o driver do leitor;~\r\n3) Baixar e instalar o SafeSign do site da imprensa oficial (http://intranet.clp.unesp.br/util/Pubnet/SafeSign/);~\r\n** OBS.: Antes de acessar o site da CertSign deve-se conectar o leitor com o e-CPF ao computador***~\r\n4) Acessar o site da imprensa oficial e testar se tudo está funcionando corretamente https://pubnet.imprensaoficial.com.br/ .~'',solucionado=''s'' WHERE id_casos=''18''', '0000-00-00 00:00:00', '2801:88:c17:100'),
(134, '', 'UPDATE caso SET solucao=''1) Baixar e instalar o pacote da Pubnet http://intranet.clp.unesp.br/util/Pubnet/Instalador_Pubnet_IOCDA3GemPlus.exe\r\n2) Baixar e instalar o driver do leitor de smartcard OmniKey/GemPC do site http://intranet.clp.unesp.br/util/Pubnet/ e escolher o driver do leitor;~\r\n3) Baixar e instalar o SafeSign do site da imprensa oficial (http://intranet.clp.unesp.br/util/Pubnet/SafeSign/);~\r\n4) Acessar o site da imprensa oficial e testar se tudo está funcionando corretamente https://pubnet.imprensaoficial.com.br/ .~'',solucionado=''s'' WHERE id_casos=''18''', '0000-00-00 00:00:00', '2801:88:c17:100'),
(135, 'Douglas Vigliaz', 'INSERT INTO caso(id_area,p_chaves,titulo,problema,solucao,solucionado,id_usuario) VALUES(''2'',UCASE(''ssh; keys; chaves''),UCASE(''3COM - CHAVES SSH''),''Passo a passo para inclusão de chaves SSH em switches 3COM.'',''OBS: **CHAVE = USUARIO***~\r\ndelete CHAVE.pub+\r\ntftp 200.145.51.68 get CHAVE.pub+\r\nsys+\r\nundo ssh user USUARIO+\r\nundo public-key peer CHAVE.pub+\r\nquit+\r\nsys+\r\npublic-key peer CHAVE.pub import sshkey flash:/CHAVE.pub+\r\nssh user USUARIO service-type all authentication-type publickey assign publickey CHAVE.pub work-directory flash:/+\r\nsave\r\n'',''s'',''3'')', '0000-00-00 00:00:00', '2801:88:c17:100');
